    test('HTTP 5xx ì—ëŸ¬ë¥¼ SERVER_ERRORë¡œ ë¶„ë¥˜í•´ì•¼ í•¨', () => {
      const serverError = new Error('HTTP 500: Internal Server Error');
      expect(classifyError(serverError)).toBe(ErrorTypes.SERVER_ERROR);
      
