    test('ì•Œ ìˆ˜ ì—†ëŠ” ì—ëŸ¬ëŠ” UNKNOWN_ERRORë¡œ ë¶„ë¥˜ë˜ì–´ì•¼ í•¨', () => {
      const unknownError = new Error('Something went wrong');
      expect(classifyError(unknownError)).toBe(ErrorTypes.UNKNOWN_ERROR);
