  describe('getErrorMessage', () => {
    test('ë„¤íŠ¸ì›Œí¬ ì—ëŸ¬ì— ëŒ€í•œ ë©”ì‹œì§€ë¥¼ ë°˜í™˜í•´ì•¼ í•¨', () => {
