import { useEffect, useRef, useCallback, useState } from 'react';
import { useSaveDocument } from './useDocuments';

const useAutoSave = ({
  document,
  content,
  title,
  enabled = true,
  interval = 3000, // 3ì´ˆ
  onSaveStart,
  onSaveSuccess,
  onSaveError
}) => {
  const [lastSaved, setLastSaved] = useState(null);
  const [saveStatus, setSaveStatus] = useState('saved'); // 'saved', 'saving', 'pending', 'error'
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  
  const saveTimeoutRef = useRef(null);
  const lastContentRef = useRef(content);
  const lastTitleRef = useRef(title);
  const isManualSaveRef = useRef(false);
  
  const saveMutation = useSaveDocument();

  // ë³€ê²½ì‚¬í•­ ê°ì§€
  useEffect(() => {
    const contentChanged = content !== lastContentRef.current;
    const titleChanged = title !== lastTitleRef.current;
    
    if (contentChanged || titleChanged) {
      setHasUnsavedChanges(true);
      setSaveStatus('pending');
      
      // ì´ì „ íƒ€ì´ë¨¸ ì·¨ì†Œ
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current);
      }
      
      // ìë™ ì €ì¥ì´ í™œì„±í™”ë˜ì–´ ìˆê³  ìˆ˜ë™ ì €ì¥ ì¤‘ì´ ì•„ë‹ ë•Œë§Œ íƒ€ì´ë¨¸ ì„¤ì •
      if (enabled && !isManualSaveRef.current) {
        saveTimeoutRef.current = setTimeout(() => {
          performAutoSave();
        }, interval);
      }
      
      lastContentRef.current = content;
      lastTitleRef.current = title;
    }
  }, [content, title, enabled, interval]);

  // ìë™ ì €ì¥ ì‹¤í–‰
  const performAutoSave = useCallback(async () => {
    if (!document || !hasUnsavedChanges) return;
    
    try {
      setSaveStatus('saving');
      onSaveStart?.();
      
      const documentToSave = {
        ...document,
        content,
        title,
        updatedAt: new Date().toISOString()
      };
      
      await saveMutation.mutateAsync(documentToSave);
      
      setHasUnsavedChanges(false);
      setSaveStatus('saved');
      setLastSaved(new Date());
      onSaveSuccess?.();
      
    } catch (error) {
      console.error('ìë™ ì €ì¥ ì‹¤íŒ¨:', error);
      setSaveStatus('error');
      onSaveError?.(error);
    }
  }, [document, content, title, hasUnsavedChanges, saveMutation, onSaveStart, onSaveSuccess, onSaveError]);

  // ìˆ˜ë™ ì €ì¥
  const manualSave = useCallback(async () => {
    if (!document) return;
    
    // ìˆ˜ë™ ì €ì¥ í”Œë˜ê·¸ ì„¤ì •
    isManualSaveRef.current = true;
    
    // ìë™ ì €ì¥ íƒ€ì´ë¨¸ ì·¨ì†Œ
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current);
      saveTimeoutRef.current = null;
    }
    
    try {
      setSaveStatus('saving');
      onSaveStart?.();
      
      const documentToSave = {
        ...document,
        content,
        title,
        updatedAt: new Date().toISOString()
      };
      
      await saveMutation.mutateAsync(documentToSave);
      
      setHasUnsavedChanges(false);
      setSaveStatus('saved');
      setLastSaved(new Date());
      onSaveSuccess?.();
      
    } catch (error) {
      console.error('ìˆ˜ë™ ì €ì¥ ì‹¤íŒ¨:', error);
      setSaveStatus('error');
      onSaveError?.(error);
    } finally {
      // ìˆ˜ë™ ì €ì¥ í”Œë˜ê·¸ í•´ì œ
      isManualSaveRef.current = false;
    }
  }, [document, content, title, saveMutation, onSaveStart, onSaveSuccess, onSaveError]);

  // ì €ì¥ ìƒíƒœ ë©”ì‹œì§€ ìƒì„±
  const getSaveStatusMessage = useCallback(() => {
    switch (saveStatus) {
      case 'saving':
        return 'ì €ì¥ ì¤‘...';
      case 'pending':
        return 'ë³€ê²½ë¨';
      case 'error':
        return 'ì €ì¥ ì‹¤íŒ¨';
      case 'saved':
        if (lastSaved) {
          const now = new Date();
          const diffMs = now - lastSaved;
          const diffMinutes = Math.floor(diffMs / 60000);
          
          if (diffMinutes < 1) {
            return 'ë°©ê¸ˆ ì €ì¥ë¨';
          } else if (diffMinutes < 60) {
