    hasUnsavedChanges,
    lastSaved,
    manualSave,
    getSaveStatusMessage,
    isAutoSaving: saveStatus === 'saving' && !isManualSaveRef.current,
    isManualSaving: saveStatus === 'saving' && isManualSaveRef.current,
  };
};

