// API í•¨ìˆ˜ë“¤
const documentAPI = {
  // ì„œë²„ì—ì„œ ë¬¸ì„œ ëª©ë¡ ê°€ì ¸ì˜¤ê¸°
  async fetchDocuments() {
    try {
      const response = await fetch('/api/posts');
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }
      return await response.json();
    } catch (error) {
