      throw error;
    }
  },

  // ì„œë²„ì—ì„œ íŠ¹ì • ë¬¸ì„œ ê°€ì ¸ì˜¤ê¸°
  async fetchDocument(id) {
    try {
      const response = await fetch(`/api/posts/${id}`);
      if (!response.ok) {
        if (response.status === 404) {
          throw new Error('ë¬¸ì„œë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
        }
