      console.error(`ì„œë²„ì—ì„œ ë¬¸ì„œ ${id} ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨:`, error);
      throw error;
    }
  },

  // ì„œë²„ì— ë¬¸ì„œ ì €ì¥
  async saveDocument(document) {
    try {
      const method = document.id ? 'PUT' : 'POST';
      const url = document.id ? `/api/posts/${document.id}` : '/api/posts';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(document),
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error('ì„œë²„ì— ë¬¸ì„œ ì €ì¥ ì‹¤íŒ¨:', error);
      throw error;
    }
  },

  // ì„œë²„ì—ì„œ ë¬¸ì„œ ì‚­ì œ
  async deleteDocument(id) {
    try {
      const response = await fetch(`/api/posts/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      return { success: true, id };
    } catch (error) {
      console.error(`ì„œë²„ì—ì„œ ë¬¸ì„œ ${id} ì‚­ì œ ì‹¤íŒ¨:`, error);
      throw error;
    }
  },
};

// IndexedDB í•¨ìˆ˜ë“¤
const localAPI = {
  async getDocuments() {
    const db = await MikiDatabase.getInstance();
    return await db.documents.orderBy('updatedAt').reverse().toArray();
  },

  async getDocument(id) {
    const db = await MikiDatabase.getInstance();
    return await db.documents.get(id);
  },

  async saveDocument(document) {
    const db = await MikiDatabase.getInstance();
    const now = new Date().toISOString();
    
    const docToSave = {
      ...document,
      updatedAt: now,
      syncStatus: 'pending', // ì„œë²„ ë™ê¸°í™” ëŒ€ê¸° ìƒíƒœ
    };

    if (document.id) {
      await db.documents.put(docToSave);
    } else {
      const id = `doc-${Date.now()}`;
      docToSave.id = id;
      await db.documents.add(docToSave);
    }

    return docToSave;
  },

  async deleteDocument(id) {
    const db = await MikiDatabase.getInstance();
    await db.documents.delete(id);
    return { success: true, id };
  },
};

// 1. ë¬¸ì„œ ëª©ë¡ ì¡°íšŒ í›…
export const useDocuments = () => {
  const queryClient = useQueryClient();

  return useQuery({
    queryKey: queryKeys.documents.lists(),
    queryFn: async () => {
      // ì˜¨ë¼ì¸ ìƒíƒœì—ì„œëŠ” ì„œë²„ ë°ì´í„° ìš°ì„ 
      if (isOnline()) {
        try {
          const serverDocs = await documentAPI.fetchDocuments();
          
          // ì„œë²„ ë°ì´í„°ë¥¼ IndexedDBì— ìºì‹œ
          const db = await MikiDatabase.getInstance();
          for (const doc of serverDocs) {
            await db.documents.put({
              ...doc,
              syncStatus: 'synced',
            });
          }
          
          return serverDocs;
        } catch (error) {
          console.warn('ì„œë²„ ë°ì´í„° ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨, ë¡œì»¬ ë°ì´í„° ì‚¬ìš©:', error);
        }
      }
      
      // ì˜¤í”„ë¼ì¸ì´ê±°ë‚˜ ì„œë²„ ì‹¤íŒ¨ ì‹œ ë¡œì»¬ ë°ì´í„° ì‚¬ìš©
      return await localAPI.getDocuments();
    },
    staleTime: 2 * 60 * 1000, // 2ë¶„
    cacheTime: 10 * 60 * 1000, // 10ë¶„
  });
};

// 2. íŠ¹ì • ë¬¸ì„œ ì¡°íšŒ í›…
export const useDocument = (id) => {
  return useQuery({
