    queryFn: async () => {
      if (!id) return null;

      // ì˜¨ë¼ì¸ ìƒíƒœì—ì„œëŠ” ì„œë²„ ë°ì´í„° ìš°ì„ 
      if (isOnline()) {
        try {
          const serverDoc = await documentAPI.fetchDocument(id);
          
          // ì„œë²„ ë°ì´í„°ë¥¼ IndexedDBì— ìºì‹œ
          const db = await MikiDatabase.getInstance();
          await db.documents.put({
            ...serverDoc,
            syncStatus: 'synced',
          });
          
          return serverDoc;
        } catch (error) {
          console.warn(`ì„œë²„ì—ì„œ ë¬¸ì„œ ${id} ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨, ë¡œì»¬ ë°ì´í„° ì‚¬ìš©:`, error);
        }
      }
      
      // ì˜¤í”„ë¼ì¸ì´ê±°ë‚˜ ì„œë²„ ì‹¤íŒ¨ ì‹œ ë¡œì»¬ ë°ì´í„° ì‚¬ìš©
      return await localAPI.getDocument(id);
