};

// 3. ë¬¸ì„œ ì €ì¥ ë®¤í…Œì´ì…˜ (Optimistic UI)
export const useSaveDocument = () => {
  const queryClient = useQueryClient();
  const { updateDocument, addDocument } = useDocumentStore();

  return useMutation({
    mutationFn: async (document) => {
      // 1. ë¡œì»¬ì— ì¦‰ì‹œ ì €ì¥ (Optimistic)
      const localDoc = await localAPI.saveDocument(document);
      
      // 2. ì˜¨ë¼ì¸ ìƒíƒœì—ì„œë§Œ ì„œë²„ ë™ê¸°í™” ì‹œë„
      if (isOnline()) {
        try {
          const serverDoc = await documentAPI.saveDocument(localDoc);
          
          // ì„œë²„ ì €ì¥ ì„±ê³µ ì‹œ ë™ê¸°í™” ìƒíƒœ ì—…ë°ì´íŠ¸
          const db = await MikiDatabase.getInstance();
          await db.documents.put({
            ...serverDoc,
            syncStatus: 'synced',
          });
          
          return serverDoc;
        } catch (error) {
          console.warn('ì„œë²„ ì €ì¥ ì‹¤íŒ¨, ë¡œì»¬ì—ë§Œ ì €ì¥ë¨:', error);
          
          // ë™ê¸°í™” íì— ì¶”ê°€
          const db = await MikiDatabase.getInstance();
          await db.syncQueue.add({
            id: `sync-${Date.now()}`,
            type: 'save',
            documentId: localDoc.id,
            data: localDoc,
            status: 'pending',
            createdAt: new Date().toISOString(),
            retryCount: 0,
          });
          
          throw error;
        }
      }
      
      return localDoc;
    },

    // Optimistic Update
    onMutate: async (newDocument) => {
      // ì§„í–‰ ì¤‘ì¸ ì¿¼ë¦¬ ì·¨ì†Œ
      await queryClient.cancelQueries({ queryKey: queryKeys.documents.all });

      // ì´ì „ ë°ì´í„° ë°±ì—…
      const previousDocuments = queryClient.getQueryData(queryKeys.documents.lists());
      const previousDocument = newDocument.id 
        ? queryClient.getQueryData(queryKeys.documents.detail(newDocument.id))
        : null;

      // Optimistic ì—…ë°ì´íŠ¸
      if (newDocument.id) {
        // ê¸°ì¡´ ë¬¸ì„œ ì—…ë°ì´íŠ¸
        queryClient.setQueryData(
          queryKeys.documents.detail(newDocument.id),
          (old) => ({ ...old, ...newDocument })
        );
        
        queryClient.setQueryData(
          queryKeys.documents.lists(),
          (old) => old?.map(doc => 
            doc.id === newDocument.id ? { ...doc, ...newDocument } : doc
          ) || []
        );
        
        // Zustand ìŠ¤í† ì–´ ì—…ë°ì´íŠ¸
        updateDocument(newDocument.id, newDocument);
      } else {
        // ìƒˆ ë¬¸ì„œ ì¶”ê°€
        const tempId = `temp-${Date.now()}`;
        const tempDoc = { ...newDocument, id: tempId };
        
        queryClient.setQueryData(
          queryKeys.documents.lists(),
          (old) => [tempDoc, ...(old || [])]
        );
        
        // Zustand ìŠ¤í† ì–´ ì—…ë°ì´íŠ¸
        addDocument(tempDoc);
      }

      return { previousDocuments, previousDocument };
    },

    // ì„±ê³µ ì‹œ
    onSuccess: (data, variables, context) => {
      // ì¿¼ë¦¬ ë¬´íš¨í™”í•˜ì—¬ ìµœì‹  ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
      queryClient.invalidateQueries({ queryKey: queryKeys.documents.all });
      
      console.log('âœ… ë¬¸ì„œ ì €ì¥ ì„±ê³µ:', data.id);
    },

    // ì‹¤íŒ¨ ì‹œ ë¡¤ë°±
    onError: (error, variables, context) => {
      const errorInfo = handleQueryError(error, 'ë¬¸ì„œ ì €ì¥');
      
      // ì´ì „ ìƒíƒœë¡œ ë¡¤ë°±
      if (context?.previousDocuments) {
        queryClient.setQueryData(queryKeys.documents.lists(), context.previousDocuments);
      }
      
      if (context?.previousDocument && variables.id) {
        queryClient.setQueryData(
          queryKeys.documents.detail(variables.id),
          context.previousDocument
        );
      }

      console.error('âŒ ë¬¸ì„œ ì €ì¥ ì‹¤íŒ¨:', errorInfo.message);
      
      // ì‚¬ìš©ìì—ê²Œ ì•Œë¦¼ (í† ìŠ¤íŠ¸ ë“±)
      // ì´ ë¶€ë¶„ì€ ì‹¤ì œ ì•Œë¦¼ ì‹œìŠ¤í…œê³¼ ì—°ê²°
    },

    // ì™„ë£Œ ì‹œ (ì„±ê³µ/ì‹¤íŒ¨ ë¬´ê´€)
    onSettled: () => {
      // ê´€ë ¨ ì¿¼ë¦¬ ë¦¬íŒ¨ì¹˜
      queryClient.invalidateQueries({ queryKey: queryKeys.documents.all });
    },
  });
};

// 4. ë¬¸ì„œ ì‚­ì œ ë®¤í…Œì´ì…˜ (Optimistic UI)
export const useDeleteDocument = () => {
  const queryClient = useQueryClient();
  const { deleteDocument } = useDocumentStore();

  return useMutation({
    mutationFn: async (id) => {
      // 1. ë¡œì»¬ì—ì„œ ì¦‰ì‹œ ì‚­ì œ (Optimistic)
