      // 2. ì˜¨ë¼ì¸ ìƒíƒœì—ì„œë§Œ ì„œë²„ ë™ê¸°í™” ì‹œë„
      if (isOnline()) {
        try {
          return await documentAPI.deleteDocument(id);
        } catch (error) {
          console.warn('ì„œë²„ ì‚­ì œ ì‹¤íŒ¨, ë¡œì»¬ì—ë§Œ ì‚­ì œë¨:', error);
          
          // ë™ê¸°í™” íì— ì¶”ê°€
          const db = await MikiDatabase.getInstance();
          await db.syncQueue.add({
            id: `sync-${Date.now()}`,
            type: 'delete',
            documentId: id,
            status: 'pending',
            createdAt: new Date().toISOString(),
            retryCount: 0,
          });
          
          throw error;
        }
      }
      
      return localResult;
    },

    // Optimistic Update
    onMutate: async (id) => {
      await queryClient.cancelQueries({ queryKey: queryKeys.documents.all });

      // ì´ì „ ë°ì´í„° ë°±ì—…
      const previousDocuments = queryClient.getQueryData(queryKeys.documents.lists());
      const previousDocument = queryClient.getQueryData(queryKeys.documents.detail(id));

      // Optimistic ì‚­ì œ
      queryClient.setQueryData(
        queryKeys.documents.lists(),
        (old) => old?.filter(doc => doc.id !== id) || []
      );
      
      queryClient.removeQueries({ queryKey: queryKeys.documents.detail(id) });
      
      // Zustand ìŠ¤í† ì–´ ì—…ë°ì´íŠ¸
      deleteDocument(id);

      return { previousDocuments, previousDocument };
    },

    onSuccess: (data, id) => {
      queryClient.invalidateQueries({ queryKey: queryKeys.documents.all });
      console.log('âœ… ë¬¸ì„œ ì‚­ì œ ì„±ê³µ:', id);
    },

    onError: (error, id, context) => {
      const errorInfo = handleQueryError(error, 'ë¬¸ì„œ ì‚­ì œ');
      
      // ë¡¤ë°±
      if (context?.previousDocuments) {
        queryClient.setQueryData(queryKeys.documents.lists(), context.previousDocuments);
      }
      
      if (context?.previousDocument) {
        queryClient.setQueryData(queryKeys.documents.detail(id), context.previousDocument);
      }

      console.error('âŒ ë¬¸ì„œ ì‚­ì œ ì‹¤íŒ¨:', errorInfo.message);
    },

    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: queryKeys.documents.all });
    },
  });
};

// 5. ë¬¸ì„œ ê²€ìƒ‰ í›…
export const useSearchDocuments = (query) => {
  return useQuery({
    queryKey: queryKeys.documents.search(query),
    queryFn: async () => {
      if (!query || query.trim().length < 2) {
        return [];
      }

      // ë¡œì»¬ ê²€ìƒ‰ (IndexedDB)
      const db = await MikiDatabase.getInstance();
      const allDocs = await db.documents.toArray();
      
      const searchTerm = query.toLowerCase();
      return allDocs.filter(doc => 
        doc.title?.toLowerCase().includes(searchTerm) ||
        doc.content?.toLowerCase().includes(searchTerm)
      );
    },
    enabled: !!query && query.trim().length >= 2,
    staleTime: 30 * 1000, // 30ì´ˆ
  });
};

