      return {
        hasPendingSync: pendingItems.length > 0,
        pendingCount: pendingItems.length,
        isOnline: isOnline(),
      };
    },
    refetchInterval: 5000, // 5ì´ˆë§ˆë‹¤ ì²´í¬
  });
};

import { useState, useEffect } from 'react';

const useResponsiveLayout = () => {
  const [isMobile, setIsMobile] = useState(false);
  const [isTablet, setIsTablet] = useState(false);
  const [activeMobilePanel, setActiveMobilePanel] = useState('list');
  const [isFullscreen, setIsFullscreen] = useState(false);

  // í™”ë©´ í¬ê¸° ê°ì§€ í•¨ìˆ˜
  const checkScreenSize = () => {
    const width = window.innerWidth;
    const newIsMobile = width <= 768;
    const newIsTablet = width > 768 && width <= 1024;
    
    setIsMobile(newIsMobile);
    setIsTablet(newIsTablet);
    
    // ëª¨ë°”ì¼ì—ì„œ ë°ìŠ¤í¬í†±ìœ¼ë¡œ ì „í™˜ ì‹œ í™œì„± íŒ¨ë„ì„ ì—ë””í„°ë¡œ ì„¤ì •
    if (!newIsMobile && isMobile) {
      setActiveMobilePanel('editor');
    }
  };

  // í™”ë©´ í¬ê¸° ë³€ê²½ ê°ì§€
  useEffect(() => {
    checkScreenSize();
    
    const handleResize = () => {
      checkScreenSize();
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [isMobile]);

  // ì „ì²´í™”ë©´ í† ê¸€
  const toggleFullscreen = () => {
    setIsFullscreen(prev => !prev);
  };

  // ëª¨ë°”ì¼ íŒ¨ë„ ì „í™˜
  const switchMobilePanel = (panel) => {
    if (isMobile) {
      setActiveMobilePanel(panel);
    }
  };

  // ì—ë””í„° íŒ¨ë„ í´ë˜ìŠ¤ ê³„ì‚°
  const getEditorPanelClass = () => {
    if (isFullscreen) {
      return "bg-white rounded shadow flex flex-col h-full";
    }
    return `bg-white rounded shadow flex flex-col ${
      isMobile ? (activeMobilePanel === 'editor' ? 'block' : 'hidden') + ' flex-grow' : 'flex-1 mx-2'
    }`;
  };

  // ë ˆì´ì•„ì›ƒ ì •ë³´ ë°˜í™˜
  return {
    isMobile,
    isTablet,
    isFullscreen,
    activeMobilePanel,
    setActiveMobilePanel: switchMobilePanel,
    toggleFullscreen,
    editorPanelClass: getEditorPanelClass()
  };
};

export default useResponsiveLayout;

