      documentUpdate: [],
      documentDelete: [],
      conflict: []
    };
  }
  
  /**
   * WebSocket ì—°ê²°
   */
  connect() {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      console.log('ğŸ”Œ WebSocket ì´ë¯¸ ì—°ê²°ë¨');
      return;
    }
    
    try {
      console.log(`ğŸ”Œ WebSocket ì—°ê²° ì‹œë„: ${this.url}`);
      this.ws = new WebSocket(this.url);
      
      this.ws.onopen = this.handleOpen.bind(this);
      this.ws.onmessage = this.handleMessage.bind(this);
      this.ws.onclose = this.handleClose.bind(this);
      this.ws.onerror = this.handleError.bind(this);
      
    } catch (error) {
      console.error('âŒ WebSocket ì—°ê²° ì‹¤íŒ¨:', error);
      this.scheduleReconnect();
    }
  }
  
  /**
   * WebSocket ì—°ê²° í•´ì œ
   */
  disconnect() {
    console.log('ğŸ”Œ WebSocket ì—°ê²° í•´ì œ');
    
    this.clearTimers();
    
    if (this.ws) {
      this.ws.onopen = null;
      this.ws.onmessage = null;
      this.ws.onclose = null;
      this.ws.onerror = null;
      
      if (this.ws.readyState === WebSocket.OPEN) {
        this.ws.close(1000, 'Client disconnect');
      }
      
      this.ws = null;
    }
    
    this.isConnected = false;
    this.reconnectAttempts = 0;
  }
  
  /**
   * ë©”ì‹œì§€ ì „ì†¡
   */
  send(type, data = {}) {
    if (!this.isConnected || !this.ws) {
      console.warn('âš ï¸ WebSocket ì—°ê²°ë˜ì§€ ì•ŠìŒ, ë©”ì‹œì§€ ì „ì†¡ ì‹¤íŒ¨');
      return false;
    }
    
    try {
      const message = {
        type,
        data,
        timestamp: new Date().toISOString()
      };
      
      this.ws.send(JSON.stringify(message));
      console.log(`ğŸ“¤ WebSocket ë©”ì‹œì§€ ì „ì†¡: ${type}`, data);
      return true;
      
    } catch (error) {
      console.error('âŒ WebSocket ë©”ì‹œì§€ ì „ì†¡ ì‹¤íŒ¨:', error);
      return false;
