  handleMessage(event) {
    try {
      const message = JSON.parse(event.data);
      console.log(`ğŸ“¥ WebSocket ë©”ì‹œì§€ ìˆ˜ì‹ : ${message.type}`, message.data);
      
      this.emit('message', message);
      
      // ë©”ì‹œì§€ íƒ€ì…ë³„ ì²˜ë¦¬
      switch (message.type) {
        case 'document_updated':
          this.emit('documentUpdate', message.data);
          break;
          
        case 'document_deleted':
          this.emit('documentDelete', message.data);
          break;
          
        case 'conflict_detected':
          this.emit('conflict', message.data);
          break;
          
        case 'pong':
          // Heartbeat ì‘ë‹µ
          break;
          
        default:
          console.log(`ğŸ¤· ì•Œ ìˆ˜ ì—†ëŠ” ë©”ì‹œì§€ íƒ€ì…: ${message.type}`);
      }
      
    } catch (error) {
      console.error('âŒ WebSocket ë©”ì‹œì§€ íŒŒì‹± ì‹¤íŒ¨:', error);
    }
  }
  
  /**
   * ì—°ê²° ì¢…ë£Œ ì²˜ë¦¬
   */
  handleClose(event) {
    console.log(`ğŸ”Œ WebSocket ì—°ê²° ì¢…ë£Œ: ${event.code} - ${event.reason}`);
    
    this.isConnected = false;
    this.clearTimers();
    
    this.emit('disconnect', { 
      code: event.code, 
      reason: event.reason,
      wasClean: event.wasClean
    });
    
    // ë¹„ì •ìƒ ì¢…ë£Œì¸ ê²½ìš° ì¬ì—°ê²° ì‹œë„
    if (event.code !== 1000 && event.code !== 1001) {
      this.scheduleReconnect();
    }
  }
  
  /**
   * ì˜¤ë¥˜ ì²˜ë¦¬
   */
  handleError(event) {
    console.error('âŒ WebSocket ì˜¤ë¥˜:', event);
    this.emit('error', { event });
  }
  
  /**
   * ì¬ì—°ê²° ìŠ¤ì¼€ì¤„ë§
   */
  scheduleReconnect() {
    if (this.reconnectAttempts >= this.maxReconnectAttempts) {
      console.error(`âŒ ìµœëŒ€ ì¬ì—°ê²° ì‹œë„ íšŸìˆ˜(${this.maxReconnectAttempts}) ì´ˆê³¼`);
      return;
    }
    
    this.reconnectAttempts++;
    const delay = Math.min(
      this.reconnectInterval * Math.pow(2, this.reconnectAttempts - 1),
      30000 // ìµœëŒ€ 30ì´ˆ
    );
    
    console.log(`ğŸ”„ ${delay}ms í›„ ì¬ì—°ê²° ì‹œë„ (${this.reconnectAttempts}/${this.maxReconnectAttempts})`);
    
    this.reconnectTimer = setTimeout(() => {
      this.connect();
    }, delay);
  }
  
  /**
   * Heartbeat ì‹œì‘
   */
  startHeartbeat() {
    this.heartbeatTimer = setInterval(() => {
      if (this.isConnected) {
        this.send('ping');
      }
    }, this.heartbeatInterval);
  }
  
  /**
   * íƒ€ì´ë¨¸ ì •ë¦¬
   */
  clearTimers() {
    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = null;
    }
    
    if (this.heartbeatTimer) {
      clearInterval(this.heartbeatTimer);
      this.heartbeatTimer = null;
    }
  }
  
  /**
   * í´ë¼ì´ì–¸íŠ¸ ID ìƒì„±/ì¡°íšŒ
   */
  getClientId() {
    let clientId = localStorage.getItem('miki_client_id');
    
    if (!clientId) {
      clientId = 'client_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
      localStorage.setItem('miki_client_id', clientId);
    }
    
    return clientId;
  }
  
  /**
   * ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ë“±ë¡
   */
  on(event, callback) {
    if (this.listeners[event]) {
      this.listeners[event].push(callback);
    }
  }
  
  /**
   * ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì œê±°
   */
  off(event, callback) {
    if (this.listeners[event]) {
      const index = this.listeners[event].indexOf(callback);
      if (index > -1) {
        this.listeners[event].splice(index, 1);
      }
    }
  }
  
  /**
   * ì´ë²¤íŠ¸ ë°œìƒ
   */
  emit(event, data) {
    if (this.listeners[event]) {
      this.listeners[event].forEach(callback => {
        try {
          callback(data);
        } catch (error) {
          console.error(`âŒ WebSocket ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì˜¤ë¥˜ (${event}):`, error);
        }
      });
    }
  }
  
  /**
   * ì—°ê²° ìƒíƒœ ì •ë³´
   */
  getStatus() {
    return {
      isConnected: this.isConnected,
      readyState: this.ws?.readyState,
      reconnectAttempts: this.reconnectAttempts,
      url: this.url
    };
  }
}

