export const defaultWebSocketAdapter = new WebSocketAdapter();

/**
 * ì„œë²„ì™€ ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ê°„ì˜ ë°ì´í„° ë™ê¸°í™”ë¥¼ ê´€ë¦¬í•˜ëŠ” í´ë˜ìŠ¤
 * ìˆ˜ì •: ì„œë²„ API êµ¬ì¡°ì— ë§ê²Œ ë™ê¸°í™” ë¡œì§ ê°œì„ 
 */
class DataSyncManager {
  constructor() {
