      if (!response.ok) {
        throw new Error(`ì„œë²„ ì‘ë‹µ ì˜¤ë¥˜: ${response.status}`);
      }
      
      const serverPosts = await response.json();
      
      // ê° ë¬¸ì„œì˜ ì „ì²´ ë‚´ìš©ì„ ê°œë³„ì ìœ¼ë¡œ ê°€ì ¸ì˜¤ê¸°
      const fullDocuments = [];
