          // ì‹¤íŒ¨í•œ ê²½ìš° ê¸°ë³¸ ì •ë³´ë§Œ ì‚¬ìš©
          fullDocuments.push({
            id: post.id,
            title: post.title || post.id,
            content: post.preview || '',
            updatedAt: post.updatedAt || new Date().toISOString(),
            source: 'server'
          });
