// DocumentSummaryManager.js
// ë¬¸ì„œë¥¼ ë§ˆí¬ë‹¤ìš´ìœ¼ë¡œ ë³€í™˜í•˜ê³  ì„¹ì…˜ë³„ë¡œ ìš”ì•½í•˜ëŠ” í´ë˜ìŠ¤

/**
 * ë¬¸ì„œ ìš”ì•½ ê´€ë¦¬ í´ë˜ìŠ¤
 * Claude APIë¥¼ ì‚¬ìš©í•˜ì—¬ ë¬¸ì„œ ìš”ì•½ ìƒì„± ë° ê´€ë¦¬
 */

// API íƒ€ì… ì„¤ì • - ë¸Œë¼ìš°ì € í˜¸í™˜ì„±ì„ ìœ„í•´ ê¸°ë³¸ê°’ ì‚¬ìš©
const API_TYPE = (typeof process !== 'undefined' && process.env?.VITE_AI_API_TYPE) || 'claude';

/**
 * ë¬¸ì„œ ìš”ì•½ ë° ë³€í™˜ì„ ì²˜ë¦¬í•˜ëŠ” í´ë˜ìŠ¤
 * AI ì²˜ë¦¬ë¥¼ ìœ„í•œ í† í° ìµœì í™”ì™€ ë¬¸ë§¥ ê´€ë¦¬ë¥¼ ë‹´ë‹¹
 */
class DocumentSummaryManager {
  constructor() {
    this.summaries = new Map(); // ë¬¸ì„œ ID ë˜ëŠ” ê²½ë¡œë¥¼ í‚¤ë¡œ, ìš”ì•½ ì •ë³´ë¥¼ ê°’ìœ¼ë¡œ ì €ì¥
    this.maxSectionLength = 1000; // ì„¹ì…˜ë‹¹ ìµœëŒ€ ë¬¸ì ìˆ˜ (ì•½ 250 í† í°)
    this.maxSummaryLength = 150; // ìš”ì•½ë‹¹ ìµœëŒ€ ë¬¸ì ìˆ˜ (ì•½ 40 í† í°)
  }

  /**
   * ë¬¸ì„œë¥¼ ì„¹ì…˜ìœ¼ë¡œ ë¶„í• í•˜ê³  ìš”ì•½ ìƒì„±
   * @param {string} documentId - ë¬¸ì„œ ì‹ë³„ì
   * @param {string} content - ë§ˆí¬ë‹¤ìš´ í˜•ì‹ì˜ ë¬¸ì„œ ë‚´ìš©
   * @returns {Object} - ì„¹ì…˜ ë° ìš”ì•½ ì •ë³´ë¥¼ í¬í•¨í•œ ê°ì²´
   */
  processDocument(documentId, content) {
    if (!content) return null;

    // ë¬¸ì„œë¥¼ í—¤ë”©ì„ ê¸°ì¤€ìœ¼ë¡œ ì„¹ì…˜ìœ¼ë¡œ ë¶„í• 
    const sections = this._splitIntoSections(content);

    // ê° ì„¹ì…˜ì— ëŒ€í•œ ìš”ì•½ ìƒì„±
    const processedSections = sections.map(section => {
      const summary = this._generateSummary(section.content);
      return {
        ...section,
        summary: summary
      };
    });

    // ë¬¸ì„œ ì „ì²´ ìš”ì•½ ìƒì„±
    const documentSummary = this._generateDocumentSummary(processedSections);

    // ê²°ê³¼ ê°ì²´ ìƒì„± ë° ìºì‹œ ì €ì¥
    const result = {
      id: documentId,
      summary: documentSummary,
      sections: processedSections,
      lastUpdated: new Date().toISOString()
    };

    this.summaries.set(documentId, result);
    return result;
  }

  /**
   * ë¬¸ì„œë¥¼ í—¤ë”©ì„ ê¸°ì¤€ìœ¼ë¡œ ì„¹ì…˜ìœ¼ë¡œ ë¶„í• 
   * @param {string} content - ë§ˆí¬ë‹¤ìš´ í˜•ì‹ì˜ ë¬¸ì„œ ë‚´ìš©
   * @returns {Array} - ì„¹ì…˜ ê°ì²´ ë°°ì—´
   */
  _splitIntoSections(content) {
    // í—¤ë”© íŒ¨í„´ (# ì œëª©, ## ì œëª© ë“±)
    const lines = content.split('\n');
    const sections = [];

    let currentHeading = "ë¬¸ì„œ";
    let currentLevel = 0;
    let currentContent = "";
    let sectionStart = 0;

    // í—¤ë”©ì´ ì—†ëŠ” ì‹œì‘ ë¶€ë¶„ì„ ì²« ë²ˆì§¸ ì„¹ì…˜ìœ¼ë¡œ ì²˜ë¦¬
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      const headingMatch = line.match(/^(#{1,6})\s+(.+)$/);

      if (headingMatch) {
        // ì²« ë²ˆì§¸ í—¤ë”©ì„ ë§Œë‚¬ì„ ë•Œ ì´ì „ ë‚´ìš©ì´ ìˆìœ¼ë©´ ì„¹ì…˜ìœ¼ë¡œ ì¶”ê°€
        if (currentContent.trim()) {
          sections.push({
            title: "ì†Œê°œ",
            level: 0,
            content: currentContent.trim(),
            startLine: sectionStart,
            endLine: i - 1
          });
        }

        // í˜„ì¬ í—¤ë”© ì •ë³´ ì—…ë°ì´íŠ¸
        currentHeading = headingMatch[2];
        currentLevel = headingMatch[1].length;
        currentContent = line;
        sectionStart = i;
      } else {
        // í—¤ë”©ì´ ì•„ë‹Œ ì¼ë°˜ í…ìŠ¤íŠ¸ ì¤„ ì¶”ê°€
        currentContent += "\n" + line;
      }

      // ë‹¤ìŒ í—¤ë”©ì„ ë§Œë‚˜ê±°ë‚˜ ë¬¸ì„œ ëì— ë„ë‹¬í•˜ë©´ ì„¹ì…˜ ì¶”ê°€
      const nextLine = i + 1 < lines.length ? lines[i + 1] : null;
      const isNextHeading = nextLine && nextLine.match(/^(#{1,6})\s+(.+)$/);

      if (isNextHeading || i === lines.length - 1) {
        if (currentContent.trim()) {
          sections.push({
            title: currentHeading,
            level: currentLevel,
            content: currentContent.trim(),
            startLine: sectionStart,
            endLine: i
          });
        }

        if (isNextHeading) {
          const nextHeadingMatch = nextLine.match(/^(#{1,6})\s+(.+)$/);
          currentHeading = nextHeadingMatch[2];
          currentLevel = nextHeadingMatch[1].length;
          currentContent = "";
          sectionStart = i + 1;
        }
      }
    }

    // ì„¹ì…˜ì´ ì—†ìœ¼ë©´ ì „ì²´ ë¬¸ì„œë¥¼ í•˜ë‚˜ì˜ ì„¹ì…˜ìœ¼ë¡œ ì²˜ë¦¬
    if (sections.length === 0 && content.trim()) {
      sections.push({
        title: "ë¬¸ì„œ",
        level: 0,
        content: content.trim(),
        startLine: 0,
        endLine: lines.length - 1
      });
    }

    return sections;
  }

  /**
   * ì„¹ì…˜ ë‚´ìš©ì— ëŒ€í•œ ê°„ë‹¨í•œ ìš”ì•½ ìƒì„± (ì‹¤ì œë¡œëŠ” AIë¥¼ í†µí•´ ìš”ì•½í•  ìˆ˜ ìˆìŒ)
   * @param {string} sectionContent - ì„¹ì…˜ ë‚´ìš©
   * @returns {string} - ì„¹ì…˜ ìš”ì•½
   */
  _generateSummary(sectionContent) {
    // ê°„ë‹¨í•œ ìš”ì•½ ë°©ì‹: ì²« ë²ˆì§¸ ë¬¸ì¥ ë˜ëŠ” ì¼ì • ê¸¸ì´ ì¶”ì¶œ
    if (!sectionContent) return "";

    // ë„ˆë¬´ ê¸´ ì„¹ì…˜ì€ ì¼ì • ê¸¸ì´ë§Œ ì‚¬ìš©
    if (sectionContent.length > this.maxSectionLength) {
      sectionContent = sectionContent.substring(0, this.maxSectionLength) + "...";
    }

    // ì²« ë²ˆì§¸ ë¬¸ì¥ ë˜ëŠ” ì²« ë²ˆì§¸ ì¤„ì„ ìš”ì•½ìœ¼ë¡œ ì‚¬ìš©
    let summary = "";
    const firstSentenceMatch = sectionContent.match(/^.+?[.!?](?:\s|$)/);

    if (firstSentenceMatch) {
      summary = firstSentenceMatch[0].trim();
    } else {
      // ë¬¸ì¥ì´ ëª…í™•í•˜ì§€ ì•Šìœ¼ë©´ ì²« ë²ˆì§¸ ì¤„ ì‚¬ìš©
      const firstLine = sectionContent.split('\n')[0].trim();
      summary = firstLine;
    }

    // ìš”ì•½ì´ ë„ˆë¬´ ê¸¸ë©´ ìë¥´ê¸°
    if (summary.length > this.maxSummaryLength) {
