      // 4ë‹¨ê³„: ë§¤í•‘ ìˆ˜ì •
      const mappingResult = await this.fixServerLocalMapping(diagnosis);
      
      // 5ë‹¨ê³„: ìµœê·¼ ë¬¸ì„œ ëª©ë¡ ì¬êµ¬ì„±
      const recentDocs = this.rebuildRecentDocuments();
      
      const finalResult = {
