      throw error;
    }
  }
  
  static async importData(jsonData) {
    try {
      const data = JSON.parse(jsonData);
      
      if (data.version !== 1) {
        throw new Error('ì§€ì›í•˜ì§€ ì•ŠëŠ” ë°ì´í„° ë²„ì „ì…ë‹ˆë‹¤.');
      }
      
      await db.transaction('rw', [db.documents, db.syncQueue], async () => {
        // Clear existing data
        await db.documents.clear();
        await db.syncQueue.clear();
        
        // Import documents
        if (data.documents?.length > 0) {
          await db.documents.bulkAdd(data.documents);
        }
        
        // Import sync queue
        if (data.syncQueue?.length > 0) {
          await db.syncQueue.bulkAdd(data.syncQueue);
        }
      });
      
      console.log('âœ… ë°ì´í„° ê°€ì ¸ì˜¤ê¸° ì™„ë£Œ');
      return { success: true };
      
    } catch (error) {
      console.error('âŒ ë°ì´í„° ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨:', error);
      return { success: false, error: error.message };
    }
  }
  
  static async getStorageInfo() {
    try {
      const docCount = await db.documents.count();
      const queueCount = await db.syncQueue.count();
      
      // Estimate storage usage (rough calculation)
      const docs = await db.documents.toArray();
      const totalSize = docs.reduce((size, doc) => {
        return size + (doc.content?.length || 0) + (doc.title?.length || 0);
      }, 0);
      
      return {
        documentCount: docCount,
        queueCount: queueCount,
        estimatedSize: totalSize,
        formattedSize: this.formatBytes(totalSize)
      };
    } catch (error) {
      console.error('âŒ ì €ì¥ì†Œ ì •ë³´ ì¡°íšŒ ì‹¤íŒ¨:', error);
      return null;
    }
  }
  
  static formatBytes(bytes) {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  }
}

