    [ErrorTypes.VALIDATION_ERROR]: {
      title: 'ì…ë ¥ ì˜¤ë¥˜',
      message: 'ì…ë ¥í•œ ì •ë³´ë¥¼ í™•ì¸í•˜ê³  ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.',
      action: 'ìˆ˜ì •í•˜ê¸°',
      canRetry: false,
    },
    [ErrorTypes.UNKNOWN_ERROR]: {
      title: 'ì•Œ ìˆ˜ ì—†ëŠ” ì˜¤ë¥˜',
      message: 'ì˜ˆìƒì¹˜ ëª»í•œ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.',
      action: 'ë‹¤ì‹œ ì‹œë„',
      canRetry: true,
