// ì¬ì‹œë„ ê°€ëŠ¥í•œ ì—ëŸ¬ì¸ì§€ í™•ì¸
export const isRetryableError = (error) => {
  const errorType = classifyError(error);
  return [ErrorTypes.NETWORK_ERROR, ErrorTypes.SERVER_ERROR, ErrorTypes.UNKNOWN_ERROR].includes(errorType);
};

// ì˜¤í”„ë¼ì¸ ìƒíƒœ ê°ì§€
export const isOfflineError = (error) => {
  return classifyError(error) === ErrorTypes.NETWORK_ERROR;
};

// í™˜ê²½ë³„ ë¡œê¹… ì œì–´ ìœ í‹¸ë¦¬í‹°
const isDevelopment = (typeof process !== 'undefined' && process.env?.NODE_ENV === 'development') || false;
const isTest = (typeof process !== 'undefined' && process.env?.NODE_ENV === 'test') || false;

// ë¡œê·¸ ë ˆë²¨ ì •ì˜
const LOG_LEVELS = {
  ERROR: 0,
  WARN: 1,
  INFO: 2,
  DEBUG: 3
};

// í˜„ì¬ ë¡œê·¸ ë ˆë²¨ ì„¤ì • (í™˜ê²½ë³€ìˆ˜ë¡œ ì œì–´ ê°€ëŠ¥)
const currentLogLevel = isDevelopment 
  ? LOG_LEVELS.DEBUG 
  : LOG_LEVELS.ERROR;

class Logger {
  constructor(module = 'App') {
    this.module = module;
  }

  error(...args) {
    if (currentLogLevel >= LOG_LEVELS.ERROR) {
      console.error(`[${this.module}]`, ...args);
    }
  }

  warn(...args) {
    if (currentLogLevel >= LOG_LEVELS.WARN) {
      console.warn(`[${this.module}]`, ...args);
    }
  }

  info(...args) {
    if (currentLogLevel >= LOG_LEVELS.INFO) {
      console.info(`[${this.module}]`, ...args);
    }
  }

  debug(...args) {
    if (currentLogLevel >= LOG_LEVELS.DEBUG && !isTest) {
      console.log(`[${this.module}]`, ...args);
    }
  }

  // ì„±ëŠ¥ ì¸¡ì •ìš©
  time(label) {
    if (currentLogLevel >= LOG_LEVELS.DEBUG && !isTest) {
      console.time(`[${this.module}] ${label}`);
    }
  }

  timeEnd(label) {
    if (currentLogLevel >= LOG_LEVELS.DEBUG && !isTest) {
      console.timeEnd(`[${this.module}] ${label}`);
    }
  }
}

// ëª¨ë“ˆë³„ ë¡œê±° ìƒì„± í•¨ìˆ˜
export const createLogger = (module) => new Logger(module);

// ê¸°ë³¸ ë¡œê±°
export const logger = new Logger('App');

// ë ˆê±°ì‹œ console.log ëŒ€ì²´ìš© (ì ì§„ì  ë§ˆì´ê·¸ë ˆì´ì…˜)
export const debugLog = (...args) => {
  if (isDevelopment && !isTest) {
    console.log('[DEBUG]', ...args);
  }
};

export default logger;

/**
 * ê²€ìƒ‰ ê²°ê³¼ ìºì‹± ë° ê´€ë¦¬ë¥¼ ìœ„í•œ ìœ í‹¸ë¦¬í‹° í´ë˜ìŠ¤
 * ë©”ëª¨ë¦¬ ë° ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ë¥¼ í™œìš©í•˜ì—¬ ê²€ìƒ‰ ê²°ê³¼ ìºì‹±
 */

class SearchCache {
  constructor(options = {}) {
    this.memoryCache = new Map(); // ë©”ëª¨ë¦¬ ë‚´ ìºì‹œ
    this.localStoragePrefix = options.localStoragePrefix || 'miki_search_cache_';
    this.defaultTtl = options.defaultTtl || 1000 * 60 * 30; // ê¸°ë³¸ TTL: 30ë¶„
    this.maxCacheSize = options.maxCacheSize || 100; // ìµœëŒ€ ìºì‹œ í•­ëª© ìˆ˜
    this.useLocalStorage = options.useLocalStorage !== false; // ë¡œì»¬ìŠ¤í† ë¦¬ì§€ ì‚¬ìš© ì—¬ë¶€
    
    // ì´ˆê¸°í™” ì‹œ ìºì‹œ ì •ë¦¬
    this.cleanup();
  }
  
  /**
   * ìºì‹œì—ì„œ ê²€ìƒ‰ ê²°ê³¼ ê°€ì ¸ì˜¤ê¸°
   * @param {string} key - ìºì‹œ í‚¤
   * @returns {Object|null} - ìºì‹œëœ ê²°ê³¼ ë˜ëŠ” null
   */
  get(key) {
    // 1. ë©”ëª¨ë¦¬ ìºì‹œ í™•ì¸
    const memoryItem = this.memoryCache.get(key);
    if (memoryItem && memoryItem.expiry > Date.now()) {
      console.log(`[SearchCache] ë©”ëª¨ë¦¬ ìºì‹œ íˆíŠ¸: ${key}`);
      return memoryItem.value;
    }
    
    // 2. ë©”ëª¨ë¦¬ ìºì‹œì— ì—†ê±°ë‚˜ ë§Œë£Œëœ ê²½ìš° ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ í™•ì¸
    if (this.useLocalStorage) {
      try {
        const storageKey = this.localStoragePrefix + key;
        const storedItem = localStorage.getItem(storageKey);
        
        if (storedItem) {
          const item = JSON.parse(storedItem);
          
          // ë§Œë£Œ í™•ì¸
          if (item.expiry > Date.now()) {
            console.log(`[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ìºì‹œ íˆíŠ¸: ${key}`);
            
            // ë©”ëª¨ë¦¬ ìºì‹œì—ë„ ì¶”ê°€
            this.memoryCache.set(key, {
              value: item.value,
              expiry: item.expiry
            });
            
            return item.value;
          } else {
            // ë§Œë£Œëœ í•­ëª© ì œê±°
            localStorage.removeItem(storageKey);
          }
        }
      } catch (e) {
        console.warn('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì•¡ì„¸ìŠ¤ ì˜¤ë¥˜:', e);
      }
    }
    
    // ìºì‹œ ë¯¸ìŠ¤
    return null;
  }
  
  /**
   * ê²€ìƒ‰ ê²°ê³¼ë¥¼ ìºì‹œì— ì €ì¥
   * @param {string} key - ìºì‹œ í‚¤
   * @param {Object} value - ì €ì¥í•  ê°’
   * @param {number} ttl - ìºì‹œ ìœ íš¨ ì‹œê°„(ms)
   */
  set(key, value, ttl = this.defaultTtl) {
    const expiry = Date.now() + ttl;
    
    // 1. ë©”ëª¨ë¦¬ ìºì‹œì— ì €ì¥
    this.memoryCache.set(key, {
      value,
      expiry
    });
    
    // ë©”ëª¨ë¦¬ ìºì‹œ í¬ê¸° ì œí•œ
    if (this.memoryCache.size > this.maxCacheSize) {
      this.pruneMemoryCache();
    }
    
    // 2. ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ì— ì €ì¥
    if (this.useLocalStorage) {
      try {
        const storageKey = this.localStoragePrefix + key;
        localStorage.setItem(storageKey, JSON.stringify({
          value,
          expiry
        }));
      } catch (e) {
        console.warn('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì €ì¥ ì˜¤ë¥˜:', e);
        // ì €ì¥ ì‹¤íŒ¨ ì‹œ ê°€ì¥ ì˜¤ë˜ëœ í•­ëª© ì œê±° í›„ ì¬ì‹œë„
        this.pruneLocalStorageCache();
        
        try {
          const storageKey = this.localStoragePrefix + key;
          localStorage.setItem(storageKey, JSON.stringify({
            value,
            expiry
          }));
        } catch (retryError) {
          // ì¬ì‹œë„ ì‹¤íŒ¨ ì‹œ ë¬´ì‹œ
          console.error('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì €ì¥ ì¬ì‹œë„ ì‹¤íŒ¨:', retryError);
        }
      }
    }
  }
  
  /**
   * íŠ¹ì • í‚¤ì˜ ìºì‹œ ì‚­ì œ
   * @param {string} key - ì‚­ì œí•  ìºì‹œ í‚¤
   */
  delete(key) {
    // ë©”ëª¨ë¦¬ ìºì‹œì—ì„œ ì‚­ì œ
    this.memoryCache.delete(key);
    
    // ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ì—ì„œ ì‚­ì œ
    if (this.useLocalStorage) {
      try {
        const storageKey = this.localStoragePrefix + key;
        localStorage.removeItem(storageKey);
      } catch (e) {
        console.warn('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì‚­ì œ ì˜¤ë¥˜:', e);
      }
    }
  }
  
  /**
   * ëª¨ë“  ìºì‹œ í•­ëª© ì œê±°
   */
  clear() {
    // ë©”ëª¨ë¦¬ ìºì‹œ ì´ˆê¸°í™”
    this.memoryCache.clear();
    
    // ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ìºì‹œ ì´ˆê¸°í™”
    if (this.useLocalStorage) {
      try {
        Object.keys(localStorage)
          .filter(key => key.startsWith(this.localStoragePrefix))
          .forEach(key => localStorage.removeItem(key));
      } catch (e) {
        console.warn('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì´ˆê¸°í™” ì˜¤ë¥˜:', e);
      }
    }
  }
  
  /**
   * ë§Œë£Œëœ ìºì‹œ í•­ëª© ì œê±°
   */
  cleanup() {
    const now = Date.now();
    
    // ë©”ëª¨ë¦¬ ìºì‹œ ì •ë¦¬
    for (const [key, item] of this.memoryCache.entries()) {
      if (item.expiry < now) {
        this.memoryCache.delete(key);
      }
    }
    
    // ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ìºì‹œ ì •ë¦¬
    if (this.useLocalStorage) {
      try {
        Object.keys(localStorage)
          .filter(key => key.startsWith(this.localStoragePrefix))
          .forEach(key => {
            try {
              const item = JSON.parse(localStorage.getItem(key));
              if (item.expiry < now) {
                localStorage.removeItem(key);
              }
            } catch (e) {
              // íŒŒì‹± ì˜¤ë¥˜ ì‹œ í•­ëª© ì œê±°
              localStorage.removeItem(key);
            }
          });
      } catch (e) {
        console.warn('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì •ë¦¬ ì˜¤ë¥˜:', e);
      }
    }
  }
  
  /**
   * ë©”ëª¨ë¦¬ ìºì‹œ ì •ë¦¬ (ê°€ì¥ ì˜¤ë˜ëœ í•­ëª©ë¶€í„° ì œê±°)
   */
  pruneMemoryCache() {
    // ë§Œë£Œ ì‹œê°„ ê¸°ì¤€ ì •ë ¬
    const entries = Array.from(this.memoryCache.entries())
      .sort((a, b) => a[1].expiry - b[1].expiry);
    
    // ì ˆë°˜ ì œê±°
    const removeCount = Math.floor(this.memoryCache.size / 2);
    entries.slice(0, removeCount).forEach(([key]) => {
      this.memoryCache.delete(key);
    });
  }
  
  /**
   * ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ìºì‹œ ì •ë¦¬ (ê°€ì¥ ì˜¤ë˜ëœ í•­ëª©ë¶€í„° ì œê±°)
   */
  pruneLocalStorageCache() {
    try {
      // ìºì‹œ í•­ëª© ì¶”ì¶œ ë° ì •ë ¬
      const cacheItems = Object.keys(localStorage)
        .filter(key => key.startsWith(this.localStoragePrefix))
        .map(key => {
          try {
            return {
              key,
              value: JSON.parse(localStorage.getItem(key))
            };
          } catch (e) {
            return { key, value: { expiry: 0 } };
          }
        })
        .sort((a, b) => a.value.expiry - b.value.expiry);
      
      // ì ˆë°˜ ì œê±°
      const removeCount = Math.floor(cacheItems.length / 2);
      cacheItems.slice(0, removeCount).forEach(item => {
        localStorage.removeItem(item.key);
      });
    } catch (e) {
      console.error('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ì •ë¦¬ ì˜¤ë¥˜:', e);
    }
  }
  
  /**
   * ê²€ìƒ‰ ì¿¼ë¦¬ì— ëŒ€í•œ ìºì‹œ í‚¤ ìƒì„±
   * @param {string} type - ê²€ìƒ‰ ìœ í˜• (keyword, ai ë“±)
   * @param {string} query - ê²€ìƒ‰ ì¿¼ë¦¬
   * @param {Object} options - ì¶”ê°€ ì˜µì…˜
   * @returns {string} - ìºì‹œ í‚¤
   */
  static generateKey(type, query, options = {}) {
    // ê¸°ë³¸ í‚¤ ìƒì„±
    let key = `${type}_${query.toLowerCase().trim()}`;
    
    // ì˜µì…˜ì— ë”°ë¥¸ í‚¤ ë³€í˜•
    if (options) {
      const optionsStr = Object.entries(options)
        .filter(([_, value]) => value !== undefined)
        .sort(([keyA], [keyB]) => keyA.localeCompare(keyB))
        .map(([key, value]) => `${key}:${JSON.stringify(value)}`)
        .join('_');
      
      if (optionsStr) {
        key += `_${optionsStr}`;
      }
    }
    
    return key;
  }
  
  /**
   * ëª¨ë“  ìºì‹œ í‚¤ ë°˜í™˜
   * @returns {Array<string>} - ëª¨ë“  ìºì‹œ í‚¤ ë°°ì—´
   */
  getAllKeys() {
    const keys = new Set();
    
    // ë©”ëª¨ë¦¬ ìºì‹œ í‚¤ ì¶”ê°€
    for (const key of this.memoryCache.keys()) {
      keys.add(key);
    }
    
    // ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ ìºì‹œ í‚¤ ì¶”ê°€
    if (this.useLocalStorage) {
      try {
        Object.keys(localStorage)
          .filter(key => key.startsWith(this.localStoragePrefix))
          .forEach(key => {
            const cacheKey = key.replace(this.localStoragePrefix, '');
            keys.add(cacheKey);
          });
      } catch (e) {
        console.warn('[SearchCache] ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ í‚¤ ì¡°íšŒ ì˜¤ë¥˜:', e);
      }
    }
    
    return Array.from(keys);
  }
}

export default SearchCache;
